import React, { useState, useEffect } from 'react';
import Chart from 'chart.js/auto';

const SimulatedDataGenerator = () => {
  const [hours, setHours] = useState(1);
  const [data, setData] = useState([]);

  useEffect(() => {
    generateData();
  }, []); // Generate data when component mounts

  const generateData = () => {
    const newData = [];
    const startDate = new Date();
    for (let i = 0; i < hours * 3600; i++) {
      const timestamp = new Date(startDate.getTime() + i * 1000).toISOString();
      const machineStatus = Math.random() < 0.5 ? 0 : 1;
      const vibration = Math.floor(Math.random() * (1000 - 100 + 1)) + 100;
      newData.push({
        id: i + 1,
        ts: timestamp,
        machine_status: machineStatus,
        vibration: vibration,
      });
    }
    setData(newData);
    drawChart(newData);
  };

  const drawChart = (data) => {
    const timestamps = data.map((entry) => entry.ts);
    const vibrations = data.map((entry) => entry.vibration);

    const ctx = document.getElementById('myChart');
    new Chart(ctx, {
      type: 'line',
      data: {
        labels: timestamps,
        datasets: [
          {
            label: 'Vibration',
            data: vibrations,
            borderColor: 'blue',
            borderWidth: 1,
            fill: false,
          },
        ],
      },
      options: {
        scales: {
          x: {
            type: 'time',
            time: {
              parser: 'iso', // Set parser to ISO format
              tooltipFormat: 'll HH:mm', // Example: 'Sep 4 2024 14:30'
            },
          },
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  };

  return (
    <div>
      <input
        type="number"
        value={hours}
        onChange={(e) => setHours(parseInt(e.target.value))}
        min="1"
        max="24"
      />
      <button onClick={generateData}>Generate Data</button>
      <canvas id="myChart" width="400" height="400"></canvas>
    </div>
  );
};

export default SimulatedDataGenerator;
