import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';
import Chart from 'chart.js';

const transformData = (timeStampDataArray) => {
  const timeArray = timeStampDataArray.map((val) => {
    const date = new Date(val.ts);
    return {
      timestamp: date.getTime(),
      hour: date.getUTCHours(),
      minutes: date.getUTCMinutes(),
      seconds: date.getUTCSeconds(),
    };
  });
  return timeArray;
};

const createTimelineData = (data, click) => {
  // Logic for filtering data based on click (1 Hr, 8 Hr, 24 Hr)
  // Return filtered data or manipulate data for rendering
};

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [time, setTime] = useState([]);
  const [click, setClick] = useState('');

  const getData = async () => {
    try {
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);
      setTime(transformData(timeStampDataArray));
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  useEffect(() => {
    // Update chart or visualization whenever data or click changes
    const timelineData = createTimelineData(data, click);
    // Example: Render chart using timelineData
  }, [data, click]);

  const buttonHandler = (e) => {
    setClick(e.target.value);
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe">
          <h5>Timeframe</h5>
        </div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {data.map((entry, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                left: `${index * 0.2}px`,
                backgroundColor:
                  entry.machine_status === 0
                    ? 'yellow'
                    : entry.machine_status === 1
                    ? 'green'
                    : 'red',
              }}
            >
              {/* Logic for rendering timestamp */}
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
