import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SummaryTable = () => {
  const [data, setData] = useState([]);
  const [summary, setSummary] = useState({
    ones: 0,
    zeros: 0,
    continuousOnes: 0,
    continuousZeros: 0,
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('/data'); // Assuming your backend endpoint for fetching data is '/data'
      setData(response.data);
      calculateSummary(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const calculateSummary = (data) => {
    let ones = 0;
    let zeros = 0;
    let continuousOnes = 0;
    let continuousZeros = 0;

    // Loop through the data to calculate summary
    for (let i = 0; i < data.length; i++) {
      if (data[i].machine_status === 1) {
        ones++;
        continuousOnes++;
        continuousZeros = 0;
      } else {
        zeros++;
        continuousZeros++;
        continuousOnes = 0;
      }
    }

    setSummary({
      ones,
      zeros,
      continuousOnes,
      continuousZeros,
    });
  };

  return (
    <div>
      <h2>Summary Table</h2>
      <table>
        <thead>
          <tr>
            <th>Category</th>
            <th>Count</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Number of Vibrations (1s)</td>
            <td>{summary.ones}</td>
          </tr>
          <tr>
            <td>Number of No Vibrations (0s)</td>
            <td>{summary.zeros}</td>
          </tr>
          <tr>
            <td>Continuous Vibrations</td>
            <td>{summary.continuousOnes}</td>
          </tr>
          <tr>
            <td>Continuous No Vibrations</td>
            <td>{summary.continuousZeros}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default SummaryTable;
