import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [timeframe, setTimeframe] = useState(null);
  const [filteredData, setFilteredData] = useState([]);
  const [time, setTime] = useState([]);

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);

      const timeArray = timeStampDataArray.map(val => {
        const date = new Date(val.ts);
        return {
          timestamp: date.getTime(),
          hour: date.getUTCHours(),
          minutes: date.getUTCMinutes(),
          seconds: date.getUTCSeconds()
        };
      });

      setTime(timeArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  useEffect(() => {
    if (timeframe) {
      const currentTime = Date.now();
      const timeThreshold = timeframe * 60 * 60 * 1000; // Convert hours to milliseconds

      const filtered = data.filter(entry => {
        const entryTime = new Date(entry.ts).getTime();
        return currentTime - entryTime <= timeThreshold;
      });

      setFilteredData(filtered);
    }
  }, [data, timeframe]);

  const buttonHandler = e => {
    const selectedTimeframe = parseInt(e.target.value, 10);
    setTimeframe(selectedTimeframe);
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value={1} className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value={8} className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value={24} className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {filteredData.map((entry, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                left: `${index * 0.2}px`,
                backgroundColor: entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'),
              }}
            >
              {(index === filteredData.length - 1 || index === 0) && (
                <div style={{ position: 'absolute', top: '100%', transform: 'translateX(-50%)' }}>
                  {new Date(time[index].timestamp).toLocaleString('en-US', {
                    hour: '2-digit',
                    minute: '2-digit',
                    second: '2-digit',
                    hour12: false,
                  })}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
