import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState();
  const [time, setTime] = useState([]);
  const [click, setClick] = useState();
  const [timeFrame, setTimeFrame] = useState(1); // Default timeframe is 1 hour

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);

      const timeArray = timeStampDataArray.map(val => {
        const date = new Date(val.ts);
        return {
          timestamp: date.getTime(),
          hour: date.getUTCHours(),
          minutes: date.getUTCMinutes(),
          seconds: date.getUTCSeconds()
        };
      });

      setTime(timeArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const buttonHandler = e => {
    setClick(e.target.value);
    setTimeFrame(Number(e.target.value.split(" ")[0])); // Extracting the numeric part of the clicked timeframe
  };

  const renderGraph = () => {
    if (!time || time.length === 0) return []; // Return empty array if time is undefined or empty

    const intervals = timeFrame * 3600 * 1000; // Converting hours to milliseconds
    const graphLength = Math.ceil((time[time.length - 1].timestamp - time[0].timestamp) / intervals);

    const graphData = new Array(graphLength).fill(0).map((val, index) => {
      const intervalStart = time[0].timestamp + index * intervals;
      const intervalEnd = intervalStart + intervals;
      const intervalReadings = data.filter(entry => entry.timestamp >= intervalStart && entry.timestamp < intervalEnd);
      return {
        timestamp: intervalStart,
        value: intervalReadings.length > 0 ? intervalReadings.reduce((sum, entry) => sum + entry.value, 0) / intervalReadings.length : 0
      };
    });

    return graphData;
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {renderGraph().map((entry, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                left: `${index * 0.2}px`,
                backgroundColor: entry.value === 0 ? 'yellow' : 'green', // Change color based on value
                height: '10px', // Adjust height as needed
                width: `${100 / renderGraph().length}%` // Distribute width equally among data points
              }}
            />
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
