// DataSimulator.js
import React, { useState } from 'react';
import '../css/DataSimulator.css';

const DataSimulator = () => {
  const [dataVisible, setDataVisible] = useState(false);
  const [data, setData] = useState(null);

  const simulateData = () => {
    const simulatedData = {
      ts: new Date().toISOString(),
      machine_status: Math.random() > 0.5 ? 1 : 0, // Simulating machine status randomly
      vibration: Math.floor(Math.random() * (1000 - 100) + 100) // Simulating vibration value within a range
    };
    setData(simulatedData);
    setDataVisible(true); // Show the data when simulated
  };

  return (
    <div className="data-simulator">
      <h2>Data Simulator</h2>
      <button className="toggle-button" onClick={() => setDataVisible(!dataVisible)}>
        {dataVisible ? "Hide Data" : "Show Data"}
      </button>
      {dataVisible && (
        <div className="simulated-data">
          <h3>Simulated Data:</h3>
          <table>
            <tbody>
              <tr>
                <td>Timestamp:</td>
                <td>{data && data.ts}</td>
              </tr>
              <tr>
                <td>Machine Status:</td>
                <td>{data && data.machine_status}</td>
              </tr>
              <tr>
                <td>Vibration:</td>
                <td>{data && data.vibration}</td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default DataSimulator;
