import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';
import Chart from 'chart.js/auto';

const TimelineGraph = () => {
  const [data, setData] = useState();
  const [time, setTime] = useState([]);
  const [click, setClick] = useState();

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);

      const timeArray = timeStampDataArray.map(val => {
        const date = new Date(val.ts);
        return {
          timestamp: date.getTime(),
          hour: date.getUTCHours(),
          minutes: date.getUTCMinutes(),
          seconds: date.getUTCSeconds()
        };
      });

      setTime(timeArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const buttonHandler = e => {
    setClick(e.target.value);
  };

  useEffect(() => {
    // Chart rendering logic
    if (!data || data.length === 0) return;

    const ctx = document.getElementById('timelineChart').getContext('2d');

    // Clear previous chart if any
    if (window.myChart !== undefined) {
      window.myChart.destroy();
    }

    const chartData = {
      labels: time.map(entry => new Date(entry.timestamp)),
      datasets: [{
        label: 'Machine Status',
        data: data.map(entry => entry.machine_status),
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1
      }]
    };

    const chartOptions = {
      scales: {
        x: {
          type: 'time',
          time: {
            unit: 'minute',
            displayFormats: {
              minute: 'DD T'
            },
            tooltipFormat: 'DD T'
          },
          title: {
            display: true,
            text: 'Timestamp'
          }
        },
        y: {
          beginAtZero: true
        }
      }
    };

    window.myChart = new Chart(ctx, {
      type: 'line',
      data: chartData,
      options: chartOptions
    });

  }, [data, time]);

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <canvas id="timelineChart" />
      </div>
    </>
  );
};

export default TimelineGraph;
