// frontend/src/components/DataDisplay.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

const DataDisplay = () => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/data'); // Assuming backend is running on port 3000
      setData(response.data);
      generateChartData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const generateChartData = (data) => {
    const timestamps = data.map(entry => entry.ts);
    const vibrations = data.map(entry => entry.vibration);

    const chartData = {
      labels: timestamps,
      datasets: [
        {
          label: 'Vibration',
          data: vibrations,
          fill: false,
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1
        }
      ]
    };

    setChartData(chartData);
  };

  return (
    <div>
      <h1>Data Display</h1>
      {chartData && (
        <Line data={chartData} />
      )}
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Timestamp</th>
            <th>Machine Status</th>
            <th>Vibration</th>
          </tr>
        </thead>
        <tbody>
          {data.map((entry) => (
            <tr key={entry.id}>
              <td>{entry.id}</td>
              <td>{entry.ts}</td>
              <td>{entry.machine_status}</td>
              <td>{entry.vibration}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataDisplay;
