import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState();
  const [time, setTime] = useState([]);
  const [click, setClick] = useState();

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);

      const timeArray = timeStampDataArray.map(val => {
        const date = new Date(val.ts);
        return {
          timestamp: date.getTime(),
          hour: date.getUTCHours(),
          minutes: date.getUTCMinutes(),
          seconds: date.getUTCSeconds()
        };
      });

      setTime(timeArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const buttonHandler = e => {
    setClick(e.target.value);
  };

  const getGraphData = () => {
    if (!click) return [];
    const now = Date.now();
    let startTimestamp;
    let step;

    if (click === '1 Hr') {
      startTimestamp = now - 3600000; // 1 hour in milliseconds
      step = 60000; // 1 minute in milliseconds
    } else if (click === '8 Hr') {
      startTimestamp = now - 28800000; // 8 hours in milliseconds
      step = 300000; // 5 minutes in milliseconds
    } else if (click === '24 Hr') {
      startTimestamp = now - 86400000; // 24 hours in milliseconds
      step = 900000; // 15 minutes in milliseconds
    }

    const graphData = [];
    for (let ts = startTimestamp; ts <= now; ts += step) {
      const matchingEntry = data.find(entry => entry.ts >= ts && entry.ts < ts + step);
      graphData.push(matchingEntry ? matchingEntry.machine_status : 0);
    }

    return graphData;
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {getGraphData().map((status, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                left: `${index * 10}px`,
                backgroundColor: status === 0 ? 'yellow' : (status === 1 ? 'green' : 'red'),
              }}
            />
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
