import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [time, setTime] = useState([]);
  const [timeframe, setTimeframe] = useState(1); // Default timeframe is 1 hour

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);

      const timeArray = timeStampDataArray.map(val => {
        const date = new Date(val.ts);
        return {
          timestamp: date.getTime(),
          hour: date.getUTCHours(),
          minutes: date.getUTCMinutes(),
          seconds: date.getUTCSeconds()
        };
      });

      setTime(timeArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const buttonHandler = timeframe => {
    setTimeframe(timeframe);
  };

  const renderDataPoints = () => {
    let interval = 0;
    switch (timeframe) {
      case 1:
        interval = 3000; // 1 hour interval
        break;
      case 8:
        interval = 24000; // 8 hours interval
        break;
      case 24:
        interval = 72000; // 24 hours interval
        break;
      default:
        interval = 3000; // Default to 1 hour interval
    }

    return data.map((entry, index) => (
      (index % interval === 0 || index === data.length - 1 || index === 0) && (
        <div
          key={index}
          className="timeline-entry"
          style={{
            left: `${index * 0.2}px`,
            backgroundColor: entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'),
          }}
        >
          <div style={{ position: 'absolute', top: '100%', transform: 'translateX(-50%)' }}>
            {new Date(time[index].timestamp).toLocaleString('en-US', {
              hour: '2-digit',
              minute: '2-digit',
              second: '2-digit',
              hour12: false,
            })}
          </div>
        </div>
      )
    ));
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list">
          <li>
            <button onClick={() => buttonHandler(1)} className={`btn ${timeframe === 1 ? 'btn-primary' : 'btn-secondary'}`}>
              1 Hr
            </button>
          </li>
          <li>
            <button onClick={() => buttonHandler(8)} className={`btn ${timeframe === 8 ? 'btn-primary' : 'btn-secondary'}`}>
              8 Hr
            </button>
          </li>
          <li>
            <button onClick={() => buttonHandler(24)} className={`btn ${timeframe === 24 ? 'btn-primary' : 'btn-secondary'}`}>
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div className="container">
        <div className="cycle-status">Cycle Status</div>
        <div className="timeline-entries">
          {renderDataPoints()}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
