import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import moment from 'moment';

const SimulateData = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    simulateData();
  }, []);

  const simulateData = () => {
    const currentTime = moment();
    const simulatedData = [];

    for (let i = 0; i < 24; i++) {
      const timestamp = currentTime.subtract(1, 'hours').toISOString();
      const machineStatus = Math.random() > 0.5 ? 1 : 0;
      const vibration = Math.floor(Math.random() * (1000 - 100) + 100);

      simulatedData.push({
        ts: timestamp,
        machine_status: machineStatus,
        vibration: vibration
      });
    }

    setData(simulatedData.reverse());
  };

  const chartData = {
    labels: data.map(entry => moment(entry.ts).format('h:mm A')),
    datasets: [
      {
        label: 'Machine Status',
        data: data.map(entry => entry.machine_status),
        fill: false,
        borderColor: 'green'
      },
      {
        label: 'Vibration',
        data: data.map(entry => entry.vibration),
        fill: false,
        borderColor: 'blue'
      }
    ]
  };

  const options = {
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Value'
        }
      }
    }
  };

  return (
    <div>
      <h2>Simulate Data for 24 Hours</h2>
      <Line data={chartData} options={options} />
    </div>
  );
};

export default SimulateData;
