import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [click, setClick] = useState('');

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const buttonHandler = (timeframe) => {
    setClick(timeframe);
  };

  const renderTime = (index) => {
    const entry = data[index];
    const date = new Date(entry.ts);
    return date.toLocaleString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: false,
    });
  };

  return (
    <>
      <div className="button-container">
        <h5>Filter data</h5>
        <ul className="button-list">
          <li>
            <button onClick={() => buttonHandler('1/2 Hr')} className={`btn ${click === '1/2 Hr' ? 'btn-primary' : 'btn-secondary'}`}>
              1/2 Hr
            </button>
          </li>
          <li>
            <button onClick={() => buttonHandler('1 Hr')} className={`btn ${click === '1 Hr' ? 'btn-primary' : 'btn-secondary'}`}>
              1 Hr
            </button>
          </li>
          {/* Add more buttons for other timeframes if needed */}
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {data.map((entry, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                left: `${index * 0.2}px`,
                backgroundColor: entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'),
              }}
            >
              {(click === '' || index === data.length - 1 || index === 0) && (
                <div style={{ position: 'absolute', top: '100%', transform: 'translateX(-50%)' }}>
                  {renderTime(index)}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
