import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [click, setClick] = useState('24 Hr'); // Default to 24 hours
  const [filteredData, setFilteredData] = useState([]);

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  useEffect(() => {
    filterData();
  }, [click, data]);

  const filterData = () => {
    let filtered = [];

    if (click === '1 Hr') {
      // Filter data for past 1 hour
      const lastTs = new Date(data[data.length - 1].ts).getTime();
      filtered = data.filter(entry => {
        const entryTs = new Date(entry.ts).getTime();
        return entryTs >= lastTs - (60 * 60 * 1000);
      });
    } else if (click === '8 Hr') {
      // Filter data for past 8 hours
      const lastTs = new Date(data[data.length - 1].ts).getTime();
      filtered = data.filter(entry => {
        const entryTs = new Date(entry.ts).getTime();
        return entryTs >= lastTs - (8 * 60 * 60 * 1000);
      });
    } else {
      // Default to 24 hours
      filtered = data;
    }

    setFilteredData(filtered);
  };

  const buttonHandler = e => {
    setClick(e.target.value);
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className={`btn ${click === '1 Hr' ? 'btn-primary' : 'btn-secondary'}`}>
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className={`btn ${click === '8 Hr' ? 'btn-primary' : 'btn-secondary'}`}>
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className={`btn ${click === '24 Hr' ? 'btn-primary' : 'btn-secondary'}`}>
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {filteredData.map((entry, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                backgroundColor: entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'),
              }}
            >
              <div style={{ position: 'absolute', top: '100%', transform: 'translateX(-50%)' }}>
                {new Date(entry.ts).toLocaleString('en-US', {
                  hour: '2-digit',
                  minute: '2-digit',
                  second: '2-digit',
                  hour12: false,
                })}
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
