import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [click, setClick] = useState('');
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();
      setData(timeStampDataArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    filterDataByTimeframe();
  }, [click, data]);

  const buttonHandler = e => {
    setClick(e.target.value);
  };

  const filterDataByTimeframe = () => {
    if (!click) {
      setFilteredData([]);
      return;
    }

    const currentTime = new Date().getTime();
    let filteredResult = [];
    switch (click) {
      case '1 Hr':
        filteredResult = data.filter(entry => new Date(entry.ts).getTime() >= currentTime - (1 * 60 * 60 * 1000));
        break;
      case '8 Hr':
        filteredResult = data.filter(entry => new Date(entry.ts).getTime() >= currentTime - (8 * 60 * 60 * 1000));
        break;
      case '24 Hr':
        filteredResult = data.filter(entry => new Date(entry.ts).getTime() >= currentTime - (24 * 60 * 60 * 1000));
        break;
      default:
        filteredResult = data;
        break;
    }
    setFilteredData(filteredResult);
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {filteredData.map((entry, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                left: `${index * 0.2}px`,
                backgroundColor: entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'),
              }}
            >
              {index === 0 && (
                <div style={{ position: 'absolute', top: '100%', transform: 'translateX(-50%)' }}>
                  {new Date(entry.ts).toLocaleString('en-US', {
                    hour: '2-digit',
                    minute: '2-digit',
                    second: '2-digit',
                    hour12: false,
                  })}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
