import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Import Axios
import '../css/FilterData.css';

const TimelineGraph = (props) => {
  const [data, setData] = useState([]);
  const [hoveredData, setHoveredData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/data'); // Fetch data from backend
        const timeStampDataArray = response.data;

        setData(timeStampDataArray.map(val => ({
          timestamp: val.ts, // Use timestamp directly from the API response
          vibration: val.vibration, // Include vibration data
          // Include other relevant data
        })));
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Function to format timestamp into 24-hour format
  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    return `${hours}:${minutes}`;
  };

  // Mouse move handler to update the hovered data point
  const handleMouseMove = (event) => {
    // Update the hovered data point every 10 minutes
    const index = Math.floor(event.nativeEvent.offsetX / 12); // Calculate the index based on mouse position
    const dataIndex = index - (index % 600); // Round to nearest multiple of 600 (10 minutes in data index)
    if (dataIndex >= 0 && dataIndex < data.length) {
      setHoveredData(data[dataIndex]);
    } else {
      setHoveredData(null);
    }
  };

  return (
    <div style={{ overflowX: 'auto', maxWidth: '100%' }}>
      <div className="button-container" style={{ display: "flex", justifyContent: "flex-end" }}>
        <h5 style={{marginRight:"-7%"}}>Filter data</h5>
        {/* Buttons for filtering data */}
      </div>
      <div>
        <div className="container" style={{ position: 'relative', top: "100px", marginLeft: "16%" }} onMouseMove={handleMouseMove}>
          <div style={{ fontSize: "20px", marginBottom: "1%" }}>Cycle Status</div>
          {data.map((entry, index) => (
            <div
              key={index}
              style={{
                position: 'absolute',
                width: '10px', // Adjust the width of the bars as needed
                height: '10px', // Adjust the height of the bars as needed
                left: `${(index * 12)}px`, // Adjust the horizontal position based on index
                backgroundColor: entry.vibration > 500 ? 'yellow' : 'green', // Adjusted condition based on vibration
              }}
            />
          ))}
          {hoveredData && (
            <div
              style={{
                position: 'absolute',
                top: '50px', // Adjust vertical position of the visual box
                left: `${(data.indexOf(hoveredData) * 12)}px`, // Adjust horizontal position based on hovered data index
                backgroundColor: 'white',
                border: '1px solid black',
                padding: '5px',
                zIndex: 9999,
              }}
            >
              <div>Vibration: {hoveredData.vibration}</div>
              <div>Time: {formatTime(hoveredData.timestamp)}</div>
              {/* Render other relevant data */}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TimelineGraph;
