// src/components/SummaryTable.js
import React from 'react';
import { Table } from 'react-bootstrap';

const SummaryTable = ({ data }) => {
  const numOnes = data.filter(entry => entry.vibration === 1).length;
  const numZeros = data.filter(entry => entry.vibration === 0).length;

  // Calculate continuous variations
  let continuousVariations = 0;
  for (let i = 0; i < data.length - 1; i++) {
    if (data[i].vibration !== data[i + 1].vibration) {
      continuousVariations++;
    }
  }

  return (
    <div>
      <h2>Summary Table</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Statistic</th>
            <th>Value</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Number of 1s</td>
            <td>{numOnes}</td>
          </tr>
          <tr>
            <td>Number of 0s</td>
            <td>{numZeros}</td>
          </tr>
          <tr>
            <td>Continuous 0s and 1s Variations</td>
            <td>{continuousVariations}</td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
};

export default SummaryTable;
