import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../CSS/SummaryTable.css'; // Import your CSS file if needed

const SummaryTable = () => {
  const [summary, setSummary] = useState({
    ones: 0,
    zeros: 0,
    continuousOnes: 0,
    continuousZeros: 0
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/data');
        const data = response.data;

        let ones = 0;
        let zeros = 0;
        let continuousOnes = 0;
        let continuousZeros = 0;
        let prevVibration = -1; // Initialize to a value outside of 0 and 1

        data.forEach(entry => {
          if (entry.vibration === 1) {
            ones++;
            if (prevVibration !== 1) continuousOnes++;
            prevVibration = 1;
          } else {
            zeros++;
            if (prevVibration !== 0) continuousZeros++;
            prevVibration = 0;
          }
        });

        setSummary({
          ones,
          zeros,
          continuousOnes,
          continuousZeros
        });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="summary-table-container">
      <h2>Summary</h2>
      <table className="summary-table">
        <thead>
          <tr>
            <th>Category</th>
            <th>Count</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Number of 1s</td>
            <td>{summary.ones}</td>
          </tr>
          <tr>
            <td>Number of 0s</td>
            <td>{summary.zeros}</td>
          </tr>
          <tr>
            <td>Continuous 1s</td>
            <td>{summary.continuousOnes}</td>
          </tr>
          <tr>
            <td>Continuous 0s</td>
            <td>{summary.continuousZeros}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default SummaryTable;
