// Generated.js
import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import '../css/TimelineGraph.css';

const Generated = () => {
  const [data, setData] = useState([]);
  const [time, setTime] = useState([]);
  const [timeframe, setTimeframe] = useState(24); // Default timeframe is 24 hours

  const simulateData = () => {
    const simulatedData = [];
    const simulatedTime = [];

    // Simulate data for 24 hours
    const startTimestamp = Date.now() - (24 * 60 * 60 * 1000);
    const interval = 10 * 60 * 1000; // 10 minutes interval
    for (let i = 0; i < 24 * 60 * 60 * 1000 / interval; i++) {
      const timestamp = startTimestamp + (i * interval);
      const date = new Date(timestamp);
      const machineStatus = Math.random() > 0.5 ? 1 : 0;
      const vibration = Math.floor(Math.random() * (1000 - 100) + 100);
      simulatedData.push({ ts: date.toISOString(), machine_status: machineStatus, vibration: vibration });
      simulatedTime.push({ timestamp: timestamp });
    }

    setData(simulatedData);
    setTime(simulatedTime);
  };

  useEffect(() => {
    simulateData();
  }, []);

  const renderDataPoints = () => {
    let interval = 0;
    switch (timeframe) {
      case 1:
        interval = 3000; // 1 hour interval
        break;
      case 8:
        interval = 24000; // 8 hours interval
        break;
      case 24:
        interval = 72000; // 24 hours interval
        break;
      default:
        interval = 72000; // Default to 24 hours interval
    }

    return data.map((entry, index) => (
      (index % interval === 0 || index === data.length - 1 || index === 0) && (
        <div
          key={index}
          className="timeline-entry"
          style={{
            left: `${index * 0.2}px`,
            backgroundColor: entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'),
          }}
        >
          <div style={{ position: 'absolute', top: '100%', transform: 'translateX(-50%)' }}>
            {new Date(time[index].timestamp).toLocaleString('en-US', {
              hour: '2-digit',
              minute: '2-digit',
              second: '2-digit',
              hour12: false,
            })}
          </div>
        </div>
      )
    ));
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list">
          <li>
            <button onClick={() => setTimeframe(1)} className={`btn ${timeframe === 1 ? 'btn-primary' : 'btn-secondary'}`}>
              1 Hr
            </button>
          </li>
          <li>
            <button onClick={() => setTimeframe(8)} className={`btn ${timeframe === 8 ? 'btn-primary' : 'btn-secondary'}`}>
              8 Hr
            </button>
          </li>
          <li>
            <button onClick={() => setTimeframe(24)} className={`btn ${timeframe === 24 ? 'btn-primary' : 'btn-secondary'}`}>
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div className="container">
        <div className="cycle-status">Cycle Status</div>
        <div className="timeline-entries">
          {renderDataPoints()}
        </div>
      </div>
    </>
  );
};

export default Generated;
