import React, { useState } from 'react';
import axios from 'axios';

const Weather = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`https://api.api-ninjas.com/v1/weather?city=${city}`, {
        headers: {
          'X-Api-Key': 'g8G0uCP3NsxZZDgMSHC3CA==Th7wHiv5tcuigdll'
        }
      });
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  return (
    <div>
      <h2>Weather Information</h2>
      <input
        type="text"
        placeholder="Enter city name"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {weatherData && (
        <div>
          <h3>Weather for {city}</h3>
          <div>Cloud Percentage: {weatherData.cloud_pct}</div>
          <div>Temperature: {weatherData.temp}째C</div>
          <div>Feels Like: {weatherData.feels_like}째C</div>
          <div>Humidity: {weatherData.humidity}%</div>
          <div>Min Temperature: {weatherData.min_temp}째C</div>
          <div>Max Temperature: {weatherData.max_temp}째C</div>
          <div>Wind Speed: {weatherData.wind_speed} m/s</div>
          <div>Wind Degrees: {weatherData.wind_degrees}</div>
          <div>Sunrise: {new Date(weatherData.sunrise * 1000).toLocaleTimeString()}</div>
          <div>Sunset: {new Date(weatherData.sunset * 1000).toLocaleTimeString()}</div>
        </div>
      )}
    </div>
  );
};

export default Weather;
