import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState();
  const [time, setTime] = useState([]);
  const [click, setClick] = useState();
  const [timeframe, setTimeframe] = useState();

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);

      const timeArray = timeStampDataArray.map(val => {
        const date = new Date(val.ts);
        return {
          timestamp: date.getTime(),
          hour: date.getUTCHours(),
          minutes: date.getUTCMinutes(),
          seconds: date.getUTCSeconds()
        };
      });

      setTime(timeArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const buttonHandler = e => {
    setClick(e.target.value);
    setTimeframe(e.target.value);
  };

  const calculateIntervals = () => {
    if (click === '1 Hr') {
      return 24;
    } else if (click === '8 Hr') {
      return 24 * 8;
    } else if (click === '24 Hr') {
      return 24 * 24;
    }
    return 1; // Default to 1 hour
  };

  const renderGraph = () => {
    const intervals = calculateIntervals();
    const graphData = Array(intervals).fill({ machine_status: 0 }); // Fill with zeros initially

    if (data && time.length > 0) {
      data.forEach((entry, index) => {
        const intervalIndex = Math.floor(index / (data.length / intervals));
        graphData[intervalIndex] = entry;
      });
    }

    return graphData.map((entry, index) => (
      <div
        key={index}
        className="timeline-entry"
        style={{
          left: `${index * (100 / intervals)}%`,
          backgroundColor: entry.machine_status === 0 ? 'yellow' : entry.machine_status === 1 ? 'green' : 'red',
        }}
      ></div>
    ));
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe">
          <h5>Timeframe</h5>
        </div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {renderGraph()}
          {timeframe && (
            <div className="timestamp-container">
              {Array.from({ length: calculateIntervals() }, (_, index) => (
                <div key={index} className="timestamp">
                  {index % 2 === 0 && (
                    <span>
                      {new Date(time[index * (time.length / calculateIntervals())].timestamp).toLocaleString('en-US', {
                        hour: '2-digit',
                        minute: '2-digit',
                        second: '2-digit',
                        hour12: false,
                      })}
                    </span>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
