import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';
import moment from 'moment';

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [time, setTime] = useState([]);
  const [click, setClick] = useState('');
  const [loading, setLoading] = useState(true);

  const getData = async () => {
    try {
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);

      const timeArray = timeStampDataArray.map(val => ({
        timestamp: new Date(val.ts).getTime(),
        hour: new Date(val.ts).getUTCHours(),
        minutes: new Date(val.ts).getUTCMinutes(),
        seconds: new Date(val.ts).getUTCSeconds()
      }));

      setTime(timeArray);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const buttonHandler = e => {
    setClick(e.target.value);
  };

  const filterDataByTimeframe = () => {
    if (click === '1 Hr') {
      return data.filter(entry => moment(entry.ts).isAfter(moment().subtract(1, 'hour')));
    } else if (click === '8 Hr') {
      return data.filter(entry => moment(entry.ts).isAfter(moment().subtract(8, 'hours')));
    } else if (click === '24 Hr') {
      return data.filter(entry => moment(entry.ts).isAfter(moment().subtract(24, 'hours')));
    } else {
      return data;
    }
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div className="container">
            <div className="cycle-status">Cycle Status</div>
            {filterDataByTimeframe().map((entry, index) => (
              <div
                key={index}
                className="timeline-entry"
                style={{
                  left: `${index * 0.2}px`,
                  backgroundColor: entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'),
                }}
              >
                {index % 3000 === 0 && (
                  <div style={{ position: 'absolute', top: '100%', transform: 'translateX(-50%)' }}>
                    {new Date(time[index].timestamp).toLocaleString('en-US', {
                      hour: '2-digit',
                      minute: '2-digit',
                      second: '2-digit',
                      hour12: false,
                    })}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
};

export default TimelineGraph;
