import React, { useState } from 'react';
import '../css/demo.css';

const TimelineGraphDemo = () => {
  const [data, setData] = useState([]);
  const [click, setClick] = useState();

  // Function to generate simulated data for 24 hours with data for each hour
  const generateData = () => {
    const startTime = new Date('2024-01-21T00:00:00');
    const simulatedData = [];
    for (let i = 0; i < 24; i++) { // Increment by 1 hour
      const timestamp = new Date(startTime.getTime() + i * 3600 * 1000); // Increment by 1 hour in milliseconds
      const machineStatus = Math.random() < 0.9 ? 1 : 0; // Assuming 90% uptime
      const vibration = Math.floor(Math.random() * 1000); // Random vibration value
      simulatedData.push({
        id: i + 1,
        ts: timestamp.toISOString(),
        machine_status: machineStatus,
        vibration: vibration,
      });
    }
    setData(simulatedData);
  };

  // Function to handle button click
  const buttonHandler = (value) => {
    setClick(value);
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={(e) => buttonHandler(e.target.value)}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
        <button onClick={generateData} className="btn btn-primary">Generate Data</button>
      </div>
      <div className="timeline-container">
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {data.map((entry, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                left: `${index * 0.2}px`,
                backgroundColor: entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'),
              }}
            >
              {((click === '1 Hr' && index % 1 === 0) ||
                (click === '8 Hr' && index % 8 === 0) ||
                (click === '24 Hr' && index % 24 === 0) ||
                index === 0 ||
                index === data.length - 1) && (
                <div style={{ position: 'absolute', top: '100%', transform: 'translateX(-50%)' }}>
                  {new Date(entry.ts).toLocaleString('en-US', {
                    hour: '2-digit',
                    minute: '2-digit',
                    second: '2-digit',
                    hour12: false,
                  })}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraphDemo;
