import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SummaryTable = () => {
  const [summaryData, setSummaryData] = useState({
    ones: 0,
    zeros: 0,
    continuousVariations: 0
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/data'); // Fetch data from backend
        const data = response.data;

        // Calculate summary statistics
        let onesCount = 0;
        let zerosCount = 0;
        let continuousVariations = 0;

        for (let i = 0; i < data.length; i++) {
          if (data[i].vibration === 1) {
            onesCount++;
          } else {
            zerosCount++;
          }

          // Check for continuous variations
          if (i > 0 && data[i].vibration !== data[i - 1].vibration) {
            continuousVariations++;
          }
        }

        setSummaryData({
          ones: onesCount,
          zeros: zerosCount,
          continuousVariations: continuousVariations
        });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Summary Table</h2>
      <table>
        <thead>
          <tr>
            <th>Statistic</th>
            <th>Value</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Number of 1s</td>
            <td>{summaryData.ones}</td>
          </tr>
          <tr>
            <td>Number of 0s</td>
            <td>{summaryData.zeros}</td>
          </tr>
          <tr>
            <td>Continuous 0s and 1s variations</td>
            <td>{summaryData.continuousVariations}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default SummaryTable;
