import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Import Axios
import '../css/FilterData.css';

const TimelineGraph = (props) => {
  const [data, setData] = useState([]);
  const [click, setClick] = useState();
  const [hoveredData, setHoveredData] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/data'); // Fetch data from backend
        const timeStampDataArray = response.data;

        setData(timeStampDataArray.map(val => ({
          timestamp: val.ts, // Use timestamp directly from the API response
          vibration: val.vibration, // Include vibration data
          otherData: val.otherData // Include other relevant data
        })));
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Button handler function
  const buttonHandler = (e) => {
    setClick(e.target.value);
  };

  // Function to format timestamp into 24-hour format
  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    return `${hours}:${minutes}`;
  };

  // Mouse move handler to update mouse position
  const handleMouseMove = (e) => {
    setMousePosition({ x: e.clientX, y: e.clientY });
  };

  // Mouse enter handler to update the hovered data point
  const handleMouseEnter = (index) => {
    setHoveredData(data[index]);
  };

  // Mouse leave handler to reset the hovered data point
  const handleMouseLeave = () => {
    setHoveredData(null);
  };

  return (
    <>
      <div className="button-container" style={{ display: "flex", justifyContent: "flex-end" }}>
        <h5 style={{marginRight:"-7%"}}>Filter data</h5>
        <ul className="button-list" style={{ listStyleType: "none", margin: 0, padding: 0, display: "flex", alignItems: "right",marginTop:"2%" }} onClick={buttonHandler}>
          <li style={{ marginRight: '15px' }}>
            <button value='1/2 Hr' className="btn btn-primary" >1/2 Hr</button>
          </li>
          <li style={{ marginRight: '25px' }}>
            <button value='1 Hr' className="btn btn-secondary">1 Hr</button>
          </li>
        </ul>
      </div>
      <div onMouseMove={handleMouseMove}>
        <div className="container" style={{ position: 'relative', top: "100px", marginLeft: "16%" }}>
          <div style={{ fontSize: "20px", marginBottom: "1%" }}>Cycle Status</div>
          {data.map((entry, index) => (
            <div
              key={index}
              style={{
                position: 'absolute',
                width: '10px', // Adjust the width of the bars as needed
                height: '10px', // Adjust the height of the bars as needed
                left: `${(index * 12)}px`, // Adjust the horizontal position based on index
                backgroundColor: entry.vibration > 500 ? 'yellow' : 'green', // Adjusted condition based on vibration
              }}
              onMouseEnter={() => handleMouseEnter(index)}
              onMouseLeave={handleMouseLeave}
            />
          ))}
          {hoveredData && (
            <div
              style={{
                position: 'fixed',
                top: `${mousePosition.y + 10}px`, // Position the data visual box below the mouse cursor
                left: `${mousePosition.x}px`, // Position the data visual box aligned with the mouse cursor
                backgroundColor: 'white',
                border: '1px solid black',
                padding: '5px',
                zIndex: 9999,
              }}
            >
              <div>Vibration: {hoveredData.vibration}</div>
              <div>Time: {formatTime(hoveredData.timestamp)}</div>
              {/* Render other relevant data */}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
