import React, { useState } from 'react';
import axios from 'axios';

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=Pune,India&appid=f145575ef6876b6ccede9725b7afd963&units=metric`);
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  return (
    <div>
      <h2>Weather Information for Pune, India</h2>
      <button onClick={handleSearch}>Get Weather</button>
      {weatherData && (
        <div>
          <h3>{weatherData.name}</h3>
          <div>Temperature: {weatherData.main.temp}Â°C</div>
          <div>Weather: {weatherData.weather[0].main}</div>
          <div>Humidity: {weatherData.main.humidity}%</div>
          <div>Wind Speed: {weatherData.wind.speed} m/s</div>
          {/* Add additional weather information as needed */}
        </div>
      )}
    </div>
  );
};

export default Weather;
