import React, { useEffect, useState } from 'react';
import Chart from 'chart.js/auto';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState();
  const [time, setTime] = useState([]);
  const [click, setClick] = useState();

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);

      const timeArray = timeStampDataArray.map(val => {
        const date = new Date(val.ts);
        return {
          timestamp: date.getTime(),
          hour: date.getUTCHours(),
          minutes: date.getUTCMinutes(),
          seconds: date.getUTCSeconds()
        };
      });

      setTime(timeArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const buttonHandler = e => {
    setClick(e.target.value);
  };

  const filterData = () => {
    if (!data) return []; // Add this line to handle undefined data

    const currentDate = new Date();
    const lastTs = new Date(data[data.length - 1].ts);
    const diff = currentDate.getTime() - lastTs.getTime();

    if (click === "1 Hr") {
      return data.filter(entry => {
        const entryDate = new Date(entry.ts);
        return currentDate.getTime() - entryDate.getTime() <= 3600000;
      });
    } else if (click === "8 Hr") {
      return data.filter(entry => {
        const entryDate = new Date(entry.ts);
        return currentDate.getTime() - entryDate.getTime() <= 28800000;
      });
    } else {
      return data;
    }
  };

  useEffect(() => {
    const ctx = document.getElementById('myChart');
    const filteredData = filterData();
    const timestamps = filteredData.map(entry => new Date(entry.ts));
    const values = filteredData.map(entry => entry.machine_status === 0 ? 'yellow' : (entry.machine_status === 1 ? 'green' : 'red'));

    new Chart(ctx, {
      type: 'line',
      data: {
        labels: timestamps,
        datasets: [{
          label: 'Machine Status',
          data: values,
          borderColor: 'rgba(255, 99, 132, 1)',
          borderWidth: 1,
          fill: false
        }]
      },
      options: {
        scales: {
          x: {
            type: 'time',
            time: {
              unit: 'minute'
            }
          }
        }
      }
    });
  }, [click, data]); // Add 'data' to dependencies

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          <canvas id="myChart" width="400" height="400"></canvas>
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
