// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

function App() {
  const [data, setData] = useState([]);
  const [summary, setSummary] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/data'); // Assuming backend is running on port 3000
      setData(response.data);
      generateSummary(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const generateSummary = (data) => {
    const ones = data.filter(entry => entry.machine_status === 1);
    const zeros = data.filter(entry => entry.machine_status === 0);
    const missing = data.filter(entry => entry.machine_status === null || entry.machine_status === undefined);

    const continuousVariations = countContinuousVariations(data);

    const summaryData = {
      numberOfOnes: ones.length,
      numberOfZeros: zeros.length,
      continuousVariations: continuousVariations
    };

    setSummary(summaryData);
  };

  const countContinuousVariations = (data) => {
    let continuousOnes = 0;
    let continuousZeros = 0;

    for (let i = 0; i < data.length; i++) {
      if (data[i].machine_status === 1) {
        continuousOnes++;
        continuousZeros = 0;
      } else if (data[i].machine_status === 0) {
        continuousZeros++;
        continuousOnes = 0;
      }
    }

    return {
      continuousOnes,
      continuousZeros
    };
  };

  const plotGraph = () => {
    const timestamps = data.map(entry => entry.ts);
    const machineStatus = data.map(entry => entry.machine_status);
    const colors = data.map(entry => {
      if (entry.machine_status === 0) return 'yellow';
      if (entry.machine_status === 1) return 'green';
      return 'red';
    });

    return {
      labels: timestamps,
      datasets: [
        {
          label: 'Machine Status',
          data: machineStatus,
          backgroundColor: colors,
          yAxisID: 'machineStatus',
          type: 'line'
        }
      ]
    };
  };


  return (
    <div>
      <h1>Data Visualization</h1>
      <div>
        <Line data={plotGraph()} />
      </div>
      {summary && (
        <div>
          <h2>Summary</h2>
          <table>
            <thead>
              <tr>
                <th>Number of Ones</th>
                <th>Number of Zeros</th>
                <th>Continuous Ones</th>
                <th>Continuous Zeros</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{summary.numberOfOnes}</td>
                <td>{summary.numberOfZeros}</td>
                <td>{summary.continuousVariations.continuousOnes}</td>
                <td>{summary.continuousVariations.continuousZeros}</td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
