// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [data, setData] = useState([]);
  const [summary, setSummary] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/data');
        setData(response.data);
        generateSummary(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Function to generate summary
  const generateSummary = (data) => {
    const summaryObj = {
      numOnes: 0,
      numZeros: 0,
      continuousVariations: 0
    };

    let prevStatus = null;
    let continuousVariations = 0;

    data.forEach((item) => {
      if (item.machine_status === 1) {
        summaryObj.numOnes++;
      } else if (item.machine_status === 0) {
        summaryObj.numZeros++;
      }

      if (prevStatus !== null && prevStatus !== item.machine_status) {
        continuousVariations++;
      }

      prevStatus = item.machine_status;
    });

    summaryObj.continuousVariations = continuousVariations;

    setSummary(summaryObj);
  };

  // Function to render timeline graph
  const renderTimelineGraph = () => {
    // Define start and end time of the graph
    const startTime = new Date(data[0].ts).getTime();
    const endTime = new Date(data[data.length - 1].ts).getTime();

    const timeline = [];

    // Loop through data to map it to the timeline
    data.forEach((item) => {
      const timestamp = new Date(item.ts).getTime();
      const diff = timestamp - startTime;

      // Calculate position on the timeline
      const position = (diff / (endTime - startTime)) * 100;

      // Determine color based on sample value
      let color;
      if (item.machine_status === 1) {
        color = 'green';
      } else if (item.machine_status === 0) {
        color = 'yellow';
      } else {
        color = 'red';
      }

      // Add data point to the timeline
      timeline.push(
        <div
          key={item.ts}
          style={{
            position: 'absolute',
            left: `${position}%`,
            width: '1%',
            height: '100%',
            backgroundColor: color,
            zIndex: 1
          }}
          title={`Timestamp: ${item.ts}, Machine Status: ${item.machine_status}, Vibration: ${item.vibration}`}
        />
      );
    });

    return timeline;
  };

  return (
    <div>
      <h1>Sample Data</h1>
      <div>
        <div style={{ position: 'relative', height: '50px', width: '100%', border: '1px solid #ccc' }}>
          {/* Render timeline */}
          {renderTimelineGraph()}
        </div>
        <div>
          {/* Display summary table */}
          <h2>Summary</h2>
          <table>
            <thead>
              <tr>
                <th>Category</th>
                <th>Count</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Number of 1s</td>
                <td>{summary.numOnes}</td>
              </tr>
              <tr>
                <td>Number of 0s</td>
                <td>{summary.numZeros}</td>
              </tr>
              <tr>
                <td>Continuous 0s and 1s Variations</td>
                <td>{summary.continuousVariations}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default App;
