import React, { useEffect, useState } from 'react';
import '../css/TimelineGraph.css';

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [time, setTime] = useState([]);
  const [click, setClick] = useState('1 Hr'); // Default timeframe

  const getData = async () => {
    try {
      // Simulate fetching data from a service
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      const timeArray = timeStampDataArray.map(val => {
        const date = new Date(val.ts);
        return {
          timestamp: date.getTime(),
          hour: date.getUTCHours(),
          minutes: date.getUTCMinutes(),
          seconds: date.getUTCSeconds()
        };
      });

      setData(timeStampDataArray);
      setTime(timeArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const buttonHandler = e => {
    setClick(e.target.value);
  };

  const getGraphData = () => {
    const timeframeInSeconds = {
      '1 Hr': 3600,
      '8 Hr': 28800,
      '24 Hr': 86400
    };

    const timeframe = timeframeInSeconds[click];

    const graphData = Array.from({ length: timeframe }, (_, index) => {
      const matchingIndex = time.findIndex(
        entry => Math.floor(entry.timestamp / 1000) === index
      );
      return matchingIndex !== -1 ? data[matchingIndex].machine_status : 0;
    });

    return graphData;
  };

  const graphData = getGraphData();

  return (
    <>
      <div className="button-container">
        <div className="Timeframe"><h5>Timeframe</h5></div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <div className="container">
          <div className="cycle-status">Cycle Status</div>
          {graphData.map((status, index) => (
            <div
              key={index}
              className="timeline-entry"
              style={{
                left: `${index * 2}px`, // Adjust the width of each timeline entry
                backgroundColor: status === 0 ? 'yellow' : (status === 1 ? 'green' : 'red'),
              }}
            />
          ))}
        </div>
      </div>
    </>
  );
};

export default TimelineGraph;
