import React, { useState } from 'react';
import axios from 'axios';

const Weather = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`https://api.api-ninjas.com/v1/weather?city=${city}`, {
        headers: {
          'X-Api-Key': 'g8G0uCP3NsxZZDgMSHC3CA==Th7wHiv5tcuigdll',
          'Content-Type': 'application/json'
        }
      });
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  return (
    <div>
      <h2>Weather Information</h2>
      <input
        type="text"
        placeholder="Enter city name"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {weatherData && (
        <div>
          <h3>{city}</h3>
          <div>Temperature: {weatherData.temperature}</div>
          <div>Weather: {weatherData.weather}</div>
          <div>Humidity: {weatherData.humidity}</div>
          <div>Wind Speed: {weatherData.windSpeed}</div>
          {/* Add additional weather information as needed */}
        </div>
      )}
    </div>
  );
};

export default Weather;
