import React, { useEffect, useState, useRef } from 'react';
import Chart from 'chart.js/auto'; // Import auto bundle for Chart.js
import '../css/TimelineGraph.css';

const transformData = (timeStampDataArray) => {
  const timeArray = timeStampDataArray.map((val) => {
    const date = new Date(val.ts);
    return {
      timestamp: date.getTime(),
      hour: date.getUTCHours(),
      minutes: date.getUTCMinutes(),
      seconds: date.getUTCSeconds(),
    };
  });
  return timeArray;
};

const createTimelineData = (data, click) => {
  // Logic for filtering data based on click (1 Hr, 8 Hr, 24 Hr)
  // Return filtered data or manipulate data for rendering
};

const TimelineGraph = () => {
  const [data, setData] = useState([]);
  const [time, setTime] = useState([]);
  const [click, setClick] = useState('');
  const chartRef = useRef(null);

  const getData = async () => {
    try {
      const response = await fetch('http://localhost:3000/data');
      const timeStampDataArray = await response.json();

      setData(timeStampDataArray);
      setTime(transformData(timeStampDataArray));
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  useEffect(() => {
    // Update chart or visualization whenever data or click changes
    const timelineData = createTimelineData(data, click);
    // Example: Render chart using timelineData

    // Get the canvas context
    const ctx = document.getElementById('timelineChart').getContext('2d');

    // Destroy previous chart instance if exists
    if (chartRef.current !== null) {
      chartRef.current.destroy();
    }

    // Generate labels for x-axis
    const labels = time.map((entry) => entry.timestamp);

    // Render the chart
    chartRef.current = new Chart(ctx, {
      type: 'line',
      data: {
        labels: labels,
        datasets: [
          {
            label: 'Machine Status',
            data: time.map((entry) => entry.machine_status),
            fill: false,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1,
          },
        ],
      },
      options: {
        scales: {
          x: {
            type: 'linear',
            position: 'bottom',
            title: {
              display: true,
              text: 'Timestamp',
            },
            ticks: {
              callback: function (value, index, values) {
                // Convert timestamp to readable format
                return new Date(value).toLocaleTimeString();
              },
            },
          },
        },
      },
    });
  }, [data, click, time]);

  const buttonHandler = (e) => {
    setClick(e.target.value);
  };

  return (
    <>
      <div className="button-container">
        <div className="Timeframe">
          <h5>Timeframe</h5>
        </div>
        <ul className="button-list" onClick={buttonHandler}>
          <li>
            <button value="1 Hr" className="btn btn-secondary">
              1 Hr
            </button>
          </li>
          <li>
            <button value="8 Hr" className="btn btn-secondary">
              8 Hr
            </button>
          </li>
          <li>
            <button value="24 Hr" className="btn btn-secondary">
              24 Hr
            </button>
          </li>
        </ul>
      </div>
      <div>
        <canvas id="timelineChart" />
      </div>
    </>
  );
};

export default TimelineGraph;
